package bharathi;


public class Loops {

	public static void main(String[] args) {

		// TODO Auto-generated method stub
		
		int n=5;
				
		if(n%3==0) 
			System.out.println("bang");
		if(n%5==0);
		System.out.println("bing");
		if(n%3==0&&n%5==0)
		System.out.println("bing"+"bang");
		if(n%3==0||n%5==0)
			System.out.println("5");
		
		
		
		
		
	}

}
