package bharathi;

public class swap3value {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=15;//x=1
		int y=1;//y=5
		int z=5;//z=15
		x=x+z;//15+5=20
		z=x-z;//20-15=5
		x=x-z;//20-5=15
		x=x+y;//5+1=6
		y=x-y;//6-1=5
		x=x-y;//6-5=1
		System.out.println("x="+x);
		System.out.println("y="+y);
		System.out.println("z="+z);
		
		
		
		
		
	}

}
